    this.state = {
      flagShowing: true,
    };
  }
  /* Mounting */

  componentWillMount() {
    console.log("component willmount");
  }

  componentDidMount() {
    console.log("componentDidMount");
  }

  componentWillUnmount() {
    console.log("componentWillUnmount");
  }

  render() {
    return (
      <div className="dem-lifecycle-container">
        <h1> Demo Life Cycle</h1>
        <Clock />
      </div>
    );
  }
}

export default DemoLifeCycle;
