import React from "react";

class Clock extends React.Component {
  constructor() {
    super(props);
    this.state = {
      dateTime: new Date(),
    };
  }

  render() {
    return (
      <div className="clock">
        <h2>This is a timer</h2>
        <h3>It is {this.state.dateTime}</h3>
      </div>
    );
  }
}

export default Clock;
