          <Router path="/" component={Home} />
        </Switch>
      </Router> */}

      {flagShowing && <DemoLifeCycle message={message} />}

      <button onClick={_changeMessage}>Change Message</button>
      <button onClick={_onClick}>Click Me</button>
      <DemoStyle />

      <hr></hr>
      <div style={{ padding: "100px" }}>
        <CustomInput />
      </div>
    </div>
  );
}

export default App;
